import React, { Component } from 'react';
import '../styles/SlideBox.scss';

export default class SlideBox extends Component {
  state = {addClass: false};
  changeTop = function() {
    this.setState({addClass:!this.state.addClass});
  };
  componentDidMount() {
    this.bind = this.changeTop.bind(this);
    window.onscroll = function() {
      if(window.pageYOffset === 0) {
        this.bind;
      }
    };
  }
  componentWillUnmount() {
    window.onscroll = null;
  }
  render() {
    let ContainerClass = ['sContainer'];
    if(this.state.addClass) {
      ContainerClass.push('view');
    }
    return (
      <div className={ContainerClass.join(' ')}>
        <div className='sContainer__home1'>
        Hi, I'm
        </div>
        <div className='sContainer__home2'>
        Michael DoHo
        </div>
        <div className='sContainer__home3'>
          I'm a front-end web developer.
        </div>  
      </div>
    )
  }
}
