import React, { Component } from 'react';
import '../styles/SlideBox.scss';

export default class SlideBox extends Component {

componentDidMount() {
  window.addEventListener("scroll",this.headerOnScroll);
}
headerOnScroll() {
  const distanceY = window.pageYOffset,
  disappear = 200,
  header = document.getElementById("sContainer");

  if (distanceY > disappear) {
    header.classList.add("view");
  } else {
    header.classList.remove("view");
  }
}
  render() {
    return (
      <div id='sContainer'>
        <div id='sContainer__home1'>
        Hi, I'm
        </div>
        <div id='sContainer__home2'>
        Michael DoHo
        </div>
        <div id='sContainer__home3'>
          I'm a front-end web developer.
        </div>  
      </div>
    )
  }
}
