import React, { Component } from 'react';
import '../styles/SlideBox.scss';

export default class SlideBox extends Component {
  constructor() {
    super();
    this.state = {class: "sContainer"};
}
componentDidMount() {
  window.onscroll = function() {
    if(window.pageYOffset === 0) {
      this.setState({class:"sContainer.view"})
    }
  };
}
componentDidMount() {
  document.addEventListener('scroll', () => {
    const isTop = window.scrollY < 100;
    if (isTop !== this.state.isTop) {
        this.setState({ isTop })
    }
  });
}

  componentWillUnmount() {
    window.onscroll = null;
  }

  render() {
    return (
      <div className={this.state.class}>
        <div className='sContainer__home1'>
        Hi, I'm
        </div>
        <div className='sContainer__home2'>
        Michael DoHo
        </div>
        <div className='sContainer__home3'>
          I'm a front-end web developer.
        </div>  
      </div>
    )
  }
}
