import React, { useRef } from "react";
import styled from "styled-components";
import * as FaIcons from "react-icons/fa";
import useOnClickOutside from "./useOnClickOutside";

const Container = styled.div`
  height: 71vh;
  width: 82vw;
  background: white;
  position: fixed;
  bottom: 70px;
  right: 109px;
  border: solid 3px;
  border-color: gainsboro;
  display: flex;
  box-shadow: 30px 30px 7px rgba(0, 0, 0, 0.5);
  z-index: 1000000;
`;

const CloseButton = styled.button`
  /* cursor: pointer; */
`;
const Container2 = styled.div`
  /* display: flex;
  flex-wrap: wrap; */
`;

const Container3 = styled.div`
  display: flex;
`;

const Movie = styled.video`
  height: auto;
  width: 52%;
  /* margin: 4% 0% 0% 4%; */
`;

const TitleBox = styled.div`

`;

const Title = styled.h1`

`;

const Description = styled.span`

`;

const Tech = styled.h2`

`;

const TechLogo = styled.img`
  height:6rem;
`;


const Modal = ({ hide, modalvideo, modalTitle, technologies1, technologies2, technologies3, technologies4, modalDescription }) => {
  const node = useRef();
  useOnClickOutside(node, hide);

  return (
    <Container ref={node}>
      <Container2>
        <CloseButton onClick={hide}>
          <FaIcons.FaTimes/>
        </CloseButton>
        <Container3>
          <Movie 
            src={modalvideo}
            controls
            loop
          />
          <TitleBox>
            <Title>{modalTitle}</Title>
            <Tech>
              Technologies:
                <TechLogo src={technologies1}/>
                <TechLogo src={technologies2}/>
                <TechLogo src={technologies3}/>
                <TechLogo src={technologies4}/>
            </Tech>
          </TitleBox>
        </Container3>
          <Description>{modalDescription}</Description>
        {/* <div className="stockVidSection">
          <video
            src={modalvideo}
            className="stockVidSection__stockDemo"
            controls
            loop
          />
        </div>
        <div className="SumSection">
          <span className="SumSection__stockTitle">{modalTitle}</span>
          <span className="SumSection__stockSum">{modalDescription}</span>
        </div>
        <span className="SumSection__stockSum">{modalDescription}</span> */}
      </Container2>
    </Container>
  );
};

export default Modal;
