import React from "react";
import ReactDOM from "react-dom";
import styled from "styled-components";
import useOnClickOutside from "../useOnClickOutside";

const Container = styled.div`
  height: 71vh;
  width: 82vw;
  background: white;
  position: fixed;
  bottom: 70px;
  right: 109px;
  border: solid 3px;
  border-color: gainsboro;
  display: flex;
  box-shadow: 30px 30px 7px rgba(0, 0, 0, 0.5);
`;

// The prop children is a way of passing react components or jsx
// from another location

const Modal = ({ modalclose, modalvideo, modalTitle, modalDescription }) =>
  useOnClickOutside(ref, () => setModalOpen(false));

ReactDOM.createPortal(
  <Container className="modal">
    <div className="stockVidSection">
      <button className="stockVidSection__offClick2" onClick={modalclose}>
        X
      </button>
      <video
        src={modalvideo}
        className="stockVidSection__stockDemo"
        controls
        loop
      />
    </div>
    <div className="SumSection">
      <span className="SumSection__stockTitle">I{modalTitle}</span>
      <span className="SumSection__stockSum">{modalDescription}</span>
    </div>
  </Container>,
  document.getElementById("modal-root")
);

export default Modal;
